import React, { useState } from "react";
import PreviewPage from "./PreviewPage";

const UserDataTest = () => {
  const [userData, setUserData] = useState({
    userName: "default name",
    userAddr: "default address",
    userTel: 100223,
    userComment: "default mention",
  });
  const updateUserData = useCallback(
    (e) => {
      const onlyNumberList = ["userTel"];
      console.log(`${e.target.name}: ${e.target.value}`);
      setUserData((prevData) => {
        if (onlyNumberList.includes(e.target.name)) {
          const targetVal = e.target.value;
          /* to ik: 때때로 숫자가 아닌것이 입력되었을 때 기존 데이터가 하나씩 지워지는 현상 발생, state관리 문제인듯 다음주 확인 할 것*/
          const onlyNum = targetVal.replace(/[^0-9]/g, "");
          console.log(targetVal);
          return { ...prevState, [e.target.name]: onlyNum };
        } else {
          return { ...prevState, [e.target.name]: e.target.value };
        }
      });
    },
    [userData],
  );
  return (
    <div className="UserDataTest">
      <h3>TEST</h3>
      <PreviewPage userData={userData} />
      <div className="inputContainer">
        <div>
          <label>이름 </label>
          <input
            type="text"
            name="userName"
            value={userData.userName}
            onChange={updateUserData}
          />
        </div>
        <div>
          <label>주소 </label>
          <input
            type="text"
            name="userAddr"
            value={userData.userAddr}
            onChange={updateUserData}
          />
        </div>
        <div>
          <label>전화 </label>
          <input
            type="text"
            name="userTel"
            value={userData.userTel}
            onChange={updateUserData}
          />
        </div>
        <div>
          <label>소개글 </label>
          <textarea
            name="userComment"
            value={userData.userComment}
            onChange={updateUserData}
          />
        </div>
      </div>
    </div>
  );
};
export default UserDataTest;
