import React, { useState, useCallback } from "react";
import PreviewPage from "./PreviewPage";

const UserDataTest = () => {
  const [userData, setUserData] = useState({
    userName: "default name",
    userAddr: "default address",
    userTel: 100223,
    userComment: "default mention",
  });
  const updateUserData = useCallback((e) => {
    const onlyNumberList = ["userTel"];
    const targetName = e.target.name;
    const targetValue = e.target.value;
    console.log(`${targetName}: ${targetValue}`);
    if (onlyNumberList.includes(targetName)) {
      targetValue = targetValue.replace(/[^0-9]/g, "");
      console.log(targetValue);
    }
    setUserData((prevData) => {
      return { ...prevData, [targetName]: targetValue };
    });
  }, []);
  return (
    <div className="UserDataTest">
      <h3>TEST</h3>
      <PreviewPage userData={userData} />
      <div className="inputContainer">
        <div>
          <label>이름 </label>
          <input
            type="text"
            name="userName"
            value={userData.userName}
            onChange={updateUserData}
          />
        </div>
        <div>
          <label>주소 </label>
          <input
            type="text"
            name="userAddr"
            value={userData.userAddr}
            onChange={updateUserData}
          />
        </div>
        <div>
          <label>전화 </label>
          <input
            type="text"
            name="userTel"
            value={userData.userTel}
            onChange={updateUserData}
          />
        </div>
        <div>
          <label>소개글 </label>
          <textarea
            name="userComment"
            value={userData.userComment}
            onChange={updateUserData}
          />
        </div>
      </div>
    </div>
  );
};
export default UserDataTest;
